import React, { Fragment, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Navbar from "../Navbar";
import { fileUpload } from "../../actions/fileAction";

const data_csv =
  "/Users/ngocho/Downloads/TKBQLMH HK2 2020-2021 xuat 21.02.CheckAttendance.xls";

const HomeScreen = (props) => {
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);
  // const { loading, error, success, file } = useSelector(
  //   (state) => state.fileUpload
  // );
  // const dispatch = useDispatch();
  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    console.log(event.target.files[0]);
    setIsFilePicked(true);
  };
  const uploadHandler = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("fileUpload", selectedFile);
    dispatch(fileUpload(formData));
  };
  return (
    <Fragment>
      <h1> Please input a FILE to examine</h1>
      <input
        type="file"
        name="file"
        // onChange={changeHandler}
      />
      ;
      {isFilePicked ? (
        <div>
          <p>Filename: {selectedFile.name}</p>
          <p>Filetype: {selectedFile.type}</p>
          <p>Size in bytes: {selectedFile.size}</p>
          <p>
            lastModifiedDate:{" "}
            {selectedFile.lastModifiedDate.toLocaleDateString()}
          </p>
        </div>
      ) : (
        <p>Select a file to show details</p>
      )}
      <div>{/* <button onClick={uploadHandler}>View</button> */}</div>
    </Fragment>
  );
};

export default HomeScreen;
