import {
  FILE_UPLOAD_REQUEST,
  FILE_UPLOAD_SUCCESS,
  FILE_UPLOAD_FAIL,
  FILE_UPLOAD_RESET,
} from "../constants/fileConstant";

const API = process.env.REACT_APP_API;

export const fileUpload = (file) => async (dispatch) => {
  try {
    dispatch({ type: FILE_UPLOAD_REQUEST });
    const { data } = await Axios.post("/api/file_upload", { file: file });
    dispatch({
      type: FILE_UPLOAD_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: FILE_UPLOAD_FAIL,
      payload:
        error.response && error.response.data.msg
          ? error.respone.data.msg
          : error,
    });
  }
};
